# ─────────── pomoćne ────────────
def normalize_base_url(u: str) -> str:
    u = u.strip().rstrip("/")
    if not u.startswith(("http://", "https://")):
        u = "https://" + u # Default na https ako nema prefiksa
    return u + "/wialon/ajax.html" if not u.endswith("/wialon/ajax.html") else u

def sha(p: str) -> str:
    return hashlib.sha256(p.encode()).hexdigest()

def load_settings() -> dict:
    if os.path.exists(SETFILE):
        try:
            with open(SETFILE, "r", encoding="utf-8") as f:
                return json.load(f)
        except Exception as e:
            st.warning(f"Greška pri učitavanju podešavanja: {e}")
    return {}

def save_settings(s: dict) -> None:
    try:
        with open(SETFILE, "w", encoding="utf-8") as f:
            json.dump(s, f, indent=2) # Dodat indent za čitljivost JSON fajla
    except Exception as e:
        st.error(f"Greška pri čuvanju podešavanja: {e}")


def wialon_call(svc: str, sid: str, params: dict | None, base: str, *, get_method=False): # Preimenovano 'get' u 'get_method' da se izbegne konflikt
    payload = {"svc": svc, "sid": sid}
    if params is not None:
        # Wialon očekuje 'params' kao JSON string čak i za GET zahteve (ako nije samo token/login)
        payload["params"] = json.dumps(params, separators=(",", ":"))

    try:
        if get_method:
            r = requests.get(base, params=payload, timeout=20)
        else:
            # Za POST, 'params' (JSON string) ide u data deo, ne u URL parametre
            post_data = {"svc": svc, "sid": sid}
            if params is not None:
                post_data["params"] = json.dumps(params, separators=(",", ":"))
            r = requests.post(base, data=post_data, timeout=20)
        
        r.raise_for_status()
        response_json = r.json()
        if isinstance(response_json, dict) and "error" in response_json:
            # Specifične Wialon greške
            error_code = response_json.get("error")
            error_text = f"Wialon API greška: {error_code}"
            # Možete dodati mapiranje kodova grešaka na poruke ako je potrebno
            # npr. if error_code == 1: error_text = "Nevalidna sesija"
            raise RuntimeError(error_text)
        return response_json
    except requests.exceptions.Timeout:
        raise RuntimeError("Wialon server nije odgovorio na vreme (timeout).")
    except requests.exceptions.ConnectionError:
        raise RuntimeError("Greška pri povezivanju sa Wialon serverom. Proverite URL i internet konekciju.")
    except requests.exceptions.HTTPError as http_err:
        raise RuntimeError(f"HTTP greška od Wialon servera: {http_err}")
    except json.JSONDecodeError:
        raise RuntimeError("Wialon server je vratio odgovor koji nije validan JSON.")
    # RuntimeError za Wialon specifične greške se već baca gore


# ─────────── Wialon API ─────────
def login_token(token: str, base: str) -> str | None:
    try:
        # Za token/login, 'token' ide kao deo JSON stringa u 'params'
        r_json = wialon_call("token/login", "", {"token": token}, base, get_method=True)
        # Ne treba 'error' in r_json provera ovde jer wialon_call to već radi
        return r_json.get("eid")
    except RuntimeError as e: # Hvata greške iz wialon_call
        st.error(f"Greška pri prijavi tokenom: {e}")
        return None
    except Exception as e: # Hvata druge neočekivane greške
        st.error(f"Neočekivana greška prilikom prijave tokenom: {e}")
        return None


def get_units(sid: str, base: str):
    res = wialon_call("core/search_items", sid,
        {"spec": {"itemsType": "avl_unit", "propName": "sys_name",
                  "propValueMask": "*", "sortType": "sys_name"},
         "force": 1, "flags": 0x01 | 0x8000, "from": 0, "to": 0}, # 0x01 za osnovne property, 0x8000 za custom property (reg_number)
        base)
    # Ne treba 'error' in res provera
    return [{"id": it["id"],
             "name": it.get("nm", "N/A"),
             "reg": it.get("prp", {}).get("reg_number", "") or it.get("prms", {}).get("reg_number", {}).get("v", "")} # Provera i u prms
            for it in res.get("items", [])]


def list_files(sid: str, uid: int, day: date, base: str):
    res = wialon_call("file/list", sid,
        {"itemId": uid, "storageType": 2, "path": "tachograph/", # storageType 2 je za datoteke jedinice
         "mask": "*", "recursive": False, "fullPath": False}, base)
    # res je lista fajlova, ne dict sa 'error' ključem ako je uspešno

    out = []
    target_day_local = day # Već je date objekat

    for f_meta in res: # res je direktno lista
        if not isinstance(f_meta, dict) or "n" not in f_meta: # Provera strukture
            continue

        file_date_match = False
        # Prioritet dajemo 'mt' (modification time), pa 'ct' (creation time)
        ts_keys_priority = ["mt", "ct"]
        for ts_key in ts_keys_priority:
            if ts_key in f_meta:
                try:
                    # Wialon timestamp-ovi su UTC
                    file_dt_utc = datetime.fromtimestamp(f_meta[ts_key], timezone.utc)
                    file_date_local = file_dt_utc.astimezone(EU_BG).date()
                    if file_date_local == target_day_local:
                        file_date_match = True
                        break 
                except (TypeError, ValueError, OSError): # Greške pri konverziji timestamp-a
                    pass # Pokušaj sledeći ključ ili metod
            
        if file_date_match:
            out.append(f_meta)
        else:
            # Fallback: Provera datuma u imenu fajla ako postoji
            date_match_in_name = DATE_RE.search(f_meta["n"])
            if date_match_in_name:
                try:
                    date_from_name = datetime.strptime(date_match_in_name.group(), "%Y%m%d").date()
                    if date_from_name == target_day_local:
                        out.append(f_meta)
                except ValueError:
                    pass # Datum u imenu nije validan

    out.sort(key=lambda x: x.get("mt", x.get("ct", 0)), reverse=True)
    return out

def get_file(sid: str, uid: int, fname: str, base: str) -> bytes | None:
    params_payload = {"itemId": uid, "storageType": 2, "path": f"tachograph/{fname}"}
    try:
        # file/get ne vraća JSON ako je uspešno, već direktno sadržaj fajla.
        # Ne koristimo wialon_call ovde jer on očekuje JSON odgovor.
        r = requests.get(base,
            params={"svc": "file/get", "sid": sid, "params": json.dumps(params_payload)}, 
            timeout=60) # Povećan timeout za download fajlova
        
        r.raise_for_status() # Proveri HTTP greške

        # Ako je odgovor JSON, to je verovatno greška od Wialona
        content_type = r.headers.get("Content-Type", "")
        if "application/json" in content_type:
            try:
                json_error = r.json()
                if "error" in json_error:
                    st.error(f"Wialon API greška pri preuzimanju fajla {fname}: {json_error}")
                    return None
            except json.JSONDecodeError:
                # Nije JSON, a Content-Type jeste? Čudno, ali tretiraj kao fajl.
                pass
        
        return r.content
    except requests.exceptions.Timeout:
        st.error(f"Timeout pri preuzimanju fajla {fname}.")
        return None
    except requests.exceptions.RequestException as e:
        st.error(f"Greška pri preuzimanju fajla {fname}: {e}")
        return None


# ─────────── SMTP & auto-task ─────────
def send_mail(subj: str, body: str, att: bytes | None,
              fname_att: str, s_settings: dict): # Preimenovano s u s_settings
    if not all(s_settings.get(k) for k in ["server", "port", "username", "password", "recipients"]):
        st.error("SMTP podešavanja nisu kompletna. Proverite Admin panel.")
        return False
    try:
        msg = EmailMessage()
        msg["Subject"] = subj
        msg["From"] = s_settings["username"]
        msg["To"] = s_settings["recipients"].split(',') # Podeli primaoce ako su zarezom odvojeni
        msg.set_content(body)
        
        if att and fname_att:
            maintype, subtype = "application", "octet-stream"
            if fname_att.lower().endswith(".zip"): subtype = "zip"
            # Možete dodati još MIME tipova ako je potrebno
            msg.add_attachment(att, maintype=maintype, subtype=subtype, filename=fname_att)
        
        with smtplib.SMTP(s_settings["server"], int(s_settings["port"]), timeout=30) as smtp:
            smtp.ehlo()
            smtp.starttls()
            smtp.ehlo()
            smtp.login(s_settings["username"], s_settings["password"])
            smtp.send_message(msg)
        return True
    except smtplib.SMTPAuthenticationError:
        st.error("SMTP greška: Autentifikacija nije uspela. Proverite korisničko ime i lozinku.")
    except smtplib.SMTPServerDisconnected:
        st.error("SMTP greška: Server je prekinuo vezu.")
    except smtplib.SMTPException as e:
        st.error(f"SMTP greška: {e}")
    except Exception as e:
        st.error(f"Neočekivana greška prilikom slanja e-maila: {e}")
        traceback.print_exc()
    return False


def schedule_nightly(app_base_url: str): # Preimenovano base u app_base_url
    tmr: threading.Timer | None = st.session_state.get(TIMERKEY)
    if tmr and tmr.is_alive(): tmr.cancel()

    s_settings = st.session_state.get("settings", {})
    if not s_settings.get("auto_send"): return

    now_eu = datetime.now(EU_BG)
    run_time_eu = t(2, 5, tzinfo=EU_BG) # 02:05h po EU_BG vremenu
    
    run_dt_eu = datetime.combine(now_eu.date(), run_time_eu)
    if now_eu.time() >= run_time_eu: # Ako je već prošlo 02:05h danas
        run_dt_eu += timedelta(days=1) # Planiraj za sutra
        
    delay_seconds = (run_dt_eu - now_eu).total_seconds()

    # Informacija o zakazivanju
    if "messages" not in st.session_state: st.session_state.messages = []
    st.session_state.messages.append({"type": "info", "content": f"Automatsko slanje zakazano za: {run_dt_eu.strftime('%Y-%m-%d %H:%M:%S %Z')}"})


    def job():
        job_start_time_str = datetime.now(EU_BG).strftime('%Y-%m-%d %H:%M:%S')
        if "messages" not in st.session_state: st.session_state.messages = []
        st.session_state.messages.append({"type": "info", "content": f"[{job_start_time_str}] Započinjanje automatskog preuzimanja DDD fajlova..."})
        
        try:
            # Uvek učitaj najsvežija podešavanja unutar thread-a
            current_s = load_settings()
            if not current_s.get("auto_send"): # Proveri ponovo, možda je isključeno
                st.session_state.messages.append({"type": "warning", "content":"Automatsko slanje je isključeno u međuvremenu. Zadatak prekinut."})
                return

            sid, wialon_base = current_s.get("sid"), current_s.get("base_url")
            
            if not sid or not wialon_base:
                st.session_state.messages.append({"type": "error", "content":"SID ili Wialon base URL nisu dostupni za automatski zadatak."})
                return

            units = get_units(sid, wialon_base)
            if not units:
                st.session_state.messages.append({"type": "info", "content":"Nema vozila za obradu u automatskom zadatku."})
                return

            prev_day_date = (datetime.now(EU_BG) - timedelta(days=1)).date()
            all_files_to_zip = [] # Lista parova (zip_path, file_data)
            
            st.session_state.messages.append({"type": "info", "content":f"Preuzimanje fajlova za dan: {prev_day_date.strftime('%d.%m.%Y')} za {len(units)} vozila."})

            for u_item in units:
                unit_name_for_path = u_item["reg"] or u_item["name"]
                unit_name_for_path = "".join(c for c in unit_name_for_path if c.isalnum() or c in (' ', '_', '-')).strip() or f"unit_{u_item['id']}"

                try:
                    files_for_unit = list_files(sid, u_item["id"], prev_day_date, wialon_base)
                    for f_meta_item in files_for_unit:
                        file_data = get_file(sid, u_item["id"], f_meta_item["n"], wialon_base)
                        if file_data:
                            zip_path = os.path.join(unit_name_for_path, f_meta_item["n"])
                            all_files_to_zip.append((zip_path, file_data))
                        else:
                            st.session_state.messages.append({"type": "warning", "content":f"Nije uspelo preuzimanje {f_meta_item['n']} za {unit_name_for_path}"})
                except Exception as e_unit_processing:
                    st.session_state.messages.append({"type": "error", "content":f"Greška pri obradi vozila {unit_name_for_path}: {e_unit_processing}"})

            if not all_files_to_zip:
                st.session_state.messages.append({"type": "info", "content":"Nema fajlova za slanje nakon pretrage."})
                subject_no_files = f"DDD fajlovi {prev_day_date.strftime('%d.%m.%Y')} - Nema podataka"
                body_no_files = "Automatski zadatak je izvršen, ali nisu pronađeni DDD fajlovi za prethodni dan."
                send_mail(subject_no_files, body_no_files, None, "", current_s)
                return

            zip_buffer = BytesIO()
            with zipfile.ZipFile(zip_buffer, "w", zipfile.ZIP_DEFLATED) as zf:
                for zip_path, data_bytes in all_files_to_zip:
                    zf.writestr(zip_path, data_bytes)
            zip_buffer.seek(0)
            
            zip_filename = f"DDD_SvaVozila_{prev_day_date.strftime('%Y%m%d')}.zip"
            mail_subject = f"DDD fajlovi za {prev_day_date.strftime('%d.%m.%Y')}"
            mail_body = f"Automatski generisana ZIP arhiva sa {len(all_files_to_zip)} DDD fajlova za dan {prev_day_date.strftime('%d.%m.%Y')}."
            
            if send_mail(mail_subject, mail_body, zip_buffer.read(), zip_filename, current_s):
                st.session_state.messages.append({"type": "success", "content":"Automatski email sa DDD fajlovima uspešno poslat."})
            else:
                 st.session_state.messages.append({"type": "error", "content":"Automatski email NIJE poslat zbog greške u slanju."})

        except RuntimeError as e_runtime_job: # Hvata greške iz Wialon poziva
            st.session_state.messages.append({"type": "error", "content":f"Wialon greška u automatskom zadatku: {e_runtime_job}"})
            traceback.print_exc() # Za serverske logove
        except Exception as e_job:
            st.session_state.messages.append({"type": "error", "content":f"Neočekivana greška u automatskom zadatku: {e_job}"})
            traceback.print_exc()
        finally:
            # Ponovno zakazivanje, ali proveravamo da li je auto_send i dalje aktivan
            final_s = load_settings()
            if final_s.get("auto_send"):
                # app_base_url se odnosi na base_url Wialon-a, ne na URL Streamlit aplikacije
                schedule_nightly(final_s.get("base_url", DEFAULT_WIALON_BASE_URL))
            else:
                st.session_state.messages.append({"type": "info", "content":"Automatsko slanje je isključeno, noćni zadatak se ne zakazuje ponovo."})

    tmr = threading.Timer(delay_seconds, job)
    tmr.daemon = True
    add_script_run_ctx(tmr) # Povezivanje threada sa Streamlit kontekstom
    tmr.start()
    st.session_state[TIMERKEY] = tmr

# ─────────── UI ───────────
def set_bg_hack(main_bg_img_base64):
    # Komentari za debugovanje loga:
    # 1. Uverite se da je `main_bg_img_base64` string koji ste uneli u LOGO_BASE64 validan i kompletan.
    #    Treba da počinje sa "data:image/png;base64," (ili jpeg, itd.)
    # 2. U browseru, otvorite Developer Tools (obično F12).
    # 3. Idite na tab "Elements". Pronađite <body> element.
    # 4. Proširite <body> da vidite ::before pseudo-element (ako ga ima).
    # 5. Kliknite na ::before. U "Styles" panelu (ili "Computed"), proverite da li se `background-image`
    #    pravilo primenjuje i da li URL izgleda kao dugačak base64 string.
    # 6. Privremeno POVEĆAJTE `opacity` na npr. 0.5 ili 1.0 da vidite da li je logo samo previše providan.
    # 7. Proverite "Console" tab u Developer Tools za eventualne greške (npr. "Failed to load resource").
    # 8. Ako ništa ne radi, probajte sa veoma malom, poznatom slikom enkodiranom u base64 da izolujete problem.
    page_bg_img_css = f'''
    <style>
    body::before {{
        content: "";
        position: fixed;
        left: 0;
        top: 0;
        width: 100vw;
        height: 100vh;
        background-image: url("{main_bg_img_base64}");
        background-repeat: no-repeat;
        background-position: center center;
        background-size: contain; /* 'contain' da ceo logo bude vidljiv, ili 'cover' da prekrije */
        opacity: 0.05; /* POVEĆAJTE OVO ZA TESTIRANJE, NPR. opacity: 0.5; */
        z-index: -1; /* Postavlja logo iza svog ostalog sadržaja */
    }}
    /* Osigurava da glavni kontejner Streamlit aplikacije bude proziran */
    .stApp {{
        background-color: rgba(0,0,0,0) !important; /* Potpuno transparentno */
    }}
    /* Opciono: Prilagodite pozadinu sidebara i headera ako je potrebno */
    [data-testid="stSidebar"], [data-testid="stHeader"] {{
        background-color: rgba(240, 242, 246, 0.8) !important; /* Svetla tema sa malo providnosti */
    }}
    html[data-theme="dark"] [data-testid="stSidebar"],
    html[data-theme="dark"] [data-testid="stHeader"] {{
         background-color: rgba(40, 43, 54, 0.8) !important; /* Tamna tema sa malo providnosti */
    }}
    </style>
    '''
    st.markdown(page_bg_img_css, unsafe_allow_html=True)

def main():
    st.set_page_config("Wialon DDD Manager", layout="wide", initial_sidebar_state="expanded")

    # Postavljanje pozadinskog loga
    if LOGO_BASE64 and "YOUR_BASE64_ENCODED_LOGO_STRING_HERE" not in LOGO_BASE64:
        set_bg_hack(LOGO_BASE64)
    else:
        # Važno: Obavesti korisnika ako placeholder nije zamenjen!
        st.sidebar.warning("POZADINSKI LOGO NIJE PODEŠEN! Zamenite placeholder string 'YOUR_BASE64_ENCODED_LOGO_STRING_HERE' u kodu sa vašim base64 stringom loga.")

    # Upravljanje porukama iz automatskog taska
    if "messages" not in st.session_state: st.session_state.messages = []
    for msg in st.session_state.messages:
        if msg["type"] == "info": st.sidebar.info(msg["content"])
        elif msg["type"] == "success": st.sidebar.success(msg["content"])
        elif msg["type"] == "warning": st.sidebar.warning(msg["content"])
        elif msg["type"] == "error": st.sidebar.error(msg["content"])
    st.session_state.messages = [] # Obriši poruke nakon prikaza

    # Novi način čitanja query parametara
    # st.query_params je dict-like objekat. .get() vraća string ili None.
    raw_query_base_url = st.query_params.get("baseUrl", DEFAULT_WIALON_BASE_URL)
    # unquote_plus je i dalje potreban ako URL može biti enkodiran
    query_base_url_unquoted = unquote_plus(raw_query_base_url)
    
    # Normalizuj URL pročitan iz query parametara ili default
    wialon_base_url_from_query_or_default = normalize_base_url(query_base_url_unquoted)
    sid_from_query = st.query_params.get("sid", None)

    if "settings" not in st.session_state:
        st.session_state["settings"] = load_settings()
    
    s_settings = st.session_state["settings"]
    # Postavi base_url iz query-ja ili default-a ako nije već u podešavanjima ili ako je prazan
    s_settings.setdefault("base_url", wialon_base_url_from_query_or_default)
    if not s_settings.get("base_url"): # Ako je bio prazan string u settings
        s_settings["base_url"] = wialon_base_url_from_query_or_default

    if sid_from_query: 
        s_settings["sid"] = sid_from_query
        # Možda sačuvati odmah ako je SID došao iz URL-a?
        # save_settings(s_settings) 

    # Inicijalno pokretanje tajmera ako je auto_send uključen i SID postoji
    # Ovo će se pozvati pri svakom rerun-u ako uslovi važe, ali schedule_nightly ima cancel logiku
    if s_settings.get("auto_send") and s_settings.get("sid"):
        if not st.session_state.get(TIMERKEY) or not st.session_state.get(TIMERKEY).is_alive():
             schedule_nightly(s_settings["base_url"])


    page = st.sidebar.radio("Navigacija", ["Preuzimanje Fajlova", "Administracija"], key="main_nav")

    current_sid = s_settings.get("sid")
    wialon_api_base = s_settings.get("base_url", DEFAULT_WIALON_BASE_URL) # Koristi default ako nije postavljen

    # Provera validnosti SID-a pre nego što se iscrtaju stranice koje zavise od njega
    sid_is_valid = False
    if current_sid:
        try:
            wialon_call("core/get_server_time", current_sid, {}, wialon_api_base) # Lagani test poziv
            sid_is_valid = True
        except RuntimeError as e: # Hvata greške iz wialon_call (npr. nevalidna sesija)
            st.sidebar.error(f"SID ({current_sid[:10]}...) nije validan ili je istekao: {e}. Molimo prijavite se ponovo.")
            s_settings.pop("sid", None) # Ukloni nevalidan SID
            current_sid = None # Ažuriraj lokalnu promenljivu
            save_settings(s_settings)
            # Otkaži tajmer ako je bio aktivan
            tmr_check: threading.Timer | None = st.session_state.get(TIMERKEY)
            if tmr_check and tmr_check.is_alive():
                tmr_check.cancel()
                st.session_state.pop(TIMERKEY, None)
                st.sidebar.info("Automatsko slanje zaustavljeno zbog nevalidnog SID-a.")
        except Exception as e_sid_check: # Druge greške (npr. mreža)
            st.sidebar.error(f"Greška pri proveri SID-a: {e_sid_check}")
            # Nećemo brisati SID za mrežne greške, možda je privremeno


    if page == "Administracija":
        st.header("🔐 Admin Panel")
        # Logika za admin lozinku...
        admin_password_set = bool(s_settings.get("admin_pw_hash"))
        if admin_password_set:
            if not st.session_state.get("admin_ok", False):
                admin_pwd_input = st.sidebar.text_input("Admin lozinka", type="password", key="admin_login_pwd")
                if st.sidebar.button("Login u Admin Panel", key="admin_login_btn"):
                    if sha(admin_pwd_input) == s_settings["admin_pw_hash"]:
                        st.session_state["admin_ok"] = True
                        st.experimental_rerun()
                    else:
                        st.sidebar.error("Pogrešna admin lozinka.")
                st.info("Unesite admin lozinku u sidebar-u za pristup administrativnim podešavanjima.")
                st.stop()
        else: # Prvo postavljanje admin lozinke
            st.sidebar.info("Admin lozinka nije postavljena. Molimo postavite je.")
            new_admin_pwd = st.sidebar.text_input("Postavi novu Admin lozinku", type="password", key="admin_set_pwd")
            if st.sidebar.button("Sačuvaj Admin lozinku", key="admin_set_pwd_btn"):
                if new_admin_pwd:
                    s_settings["admin_pw_hash"] = sha(new_admin_pwd)
                    save_settings(s_settings)
                    st.session_state["admin_ok"] = True # Automatski login
                    st.sidebar.success("Admin lozinka uspešno postavljena.")
                    st.experimental_rerun()
                else:
                    st.sidebar.error("Admin lozinka ne može biti prazna.")
            st.stop()

        # Admin sadržaj
        with st.expander("Promena Admin Lozinke", expanded=False):
            change_admin_pwd_input = st.text_input("Nova admin lozinka", type="password", key="admin_change_pwd_input")
            if st.button("Sačuvaj novu admin lozinku", key="admin_change_pwd_btn"):
                if change_admin_pwd_input:
                    s_settings["admin_pw_hash"] = sha(change_admin_pwd_input)
                    save_settings(s_settings)
                    st.success("Admin lozinka uspešno promenjena.")
                else:
                    st.error("Nova lozinka ne može biti prazna.")
        
        st.subheader("Wialon Podešavanja")
        s_settings["base_url"] = normalize_base_url(st.text_input("Wialon Base URL", s_settings.get("base_url", DEFAULT_WIALON_BASE_URL), key="wialon_url_admin"))
        s_settings["token"] = st.text_input("Wialon Token (za prijavu)", s_settings.get("token", DEFAULT_TOKEN), type="password", key="wialon_token_admin", help="Token za automatsku prijavu ako SID istekne.")
        
        st.subheader("SMTP Podešavanja")
        s_settings["server"] = st.text_input("SMTP Server", s_settings.get("server", ""), key="smtp_server")
        s_settings["port"]   = st.text_input("SMTP Port",   s_settings.get("port", "587"), key="smtp_port")
        s_settings["username"]= st.text_input("SMTP Korisničko ime", s_settings.get("username", ""), key="smtp_user")
        s_settings["password"]= st.text_input("SMTP Lozinka", s_settings.get("password", ""), type="password", key="smtp_pass")
        s_settings["recipients"]= st.text_input("Primaoci E-maila (odvojeni zarezom)", s_settings.get("recipients", ""), key="smtp_recipients", help="npr. Pera <pera@ primer.com>, mika@primer.com")

        auto_send_current_val = s_settings.get("auto_send", False)
        auto_send_new_val = st.checkbox("Noćno automatsko slanje e-maila (oko 02:05h)", value=auto_send_current_val, key="auto_send_toggle")
        if auto_send_new_val != auto_send_current_val: # Ako je došlo do promene
            s_settings["auto_send"] = auto_send_new_val
            if auto_send_new_val and current_sid: # Ako je uključeno i SID postoji
                schedule_nightly(s_settings["base_url"])
                st.info("Automatsko slanje aktivirano i zakazano.")
            elif not auto_send_new_val: # Ako je isključeno
                tmr_admin: threading.Timer | None = st.session_state.get(TIMERKEY)
                if tmr_admin and tmr_admin.is_alive():
                    tmr_admin.cancel()
                    st.session_state.pop(TIMERKEY, None)
                st.info("Automatsko slanje deaktivirano.")
            # Nema potrebe za rerun ovde, save će to uraditi

        admin_col1, admin_col2, admin_col3 = st.columns(3)
        with admin_col1:
            if st.button("Sačuvaj Podešavanja", key="admin_save_btn", type="primary"):
                save_settings(s_settings)
                st.success("Podešavanja su sačuvana.")
                # Ako je auto_send uključen, osiguraj da je tajmer pokrenut
                if s_settings.get("auto_send") and current_sid:
                     if not st.session_state.get(TIMERKEY) or not st.session_state.get(TIMERKEY).is_alive():
                        schedule_nightly(s_settings["base_url"])
                st.experimental_rerun() # Osveži ceo UI
        with admin_col2:
            if st.button("Testiraj E-mail", key="admin_test_email_btn"):
                if send_mail("Test Wialon DDD Manager", "Ovo je test poruka SMTP podešavanja.", None, "", s_settings):
                    st.success("Test e-mail uspešno poslat!")
                # Greška se prikazuje unutar send_mail
        with admin_col3:
            if st.button("Odjava iz Admin Panela", key="admin_logout_btn"):
                st.session_state.pop("admin_ok", None)
                st.experimental_rerun()

    elif page == "Preuzimanje Fajlova":
        st.header("📁 Preuzimanje DDD Fajlova")

        if not current_sid or not sid_is_valid: # Ako SID ne postoji ili nije validan
            st.subheader("Prijava na Wialon")
            login_token_val = s_settings.get("token", DEFAULT_TOKEN)
            if not login_token_val:
                 st.warning("Wialon token nije podešen u Admin panelu. Neophodan je za prijavu.")
            
            if st.button("Prijavi se Wialon Tokenom", key="manual_login_btn", disabled=not login_token_val, type="primary"):
                sid_new = login_token(login_token_val, wialon_api_base)
                if sid_new:
                    s_settings["sid"] = sid_new
                    save_settings(s_settings)
                    st.success("Uspešno prijavljen na Wialon!")
                    if s_settings.get("auto_send"): # Ako je auto_send aktivan, pokreni tajmer
                        schedule_nightly(s_settings["base_url"])
                    st.experimental_rerun()
                # Greška se prikazuje unutar login_token
            st.markdown("---")
            st.info("SID sesija nije aktivna ili je istekla. Koristite dugme iznad za prijavu. Wialon Token i URL se podešavaju u Admin panelu.")
            st.stop()
        
        # Ako smo ovde, SID je validan
        try:
            units = get_units(current_sid, wialon_api_base)
            if not units:
                st.warning("Nema dostupnih vozila na Wialon nalogu.")
                st.stop()
        except RuntimeError as e:
            st.error(f"Wialon API greška pri dobavljanju vozila: {e}")
            st.stop()
        except Exception as e:
            st.error(f"Neočekivana greška pri dobavljanju vozila: {e}")
            traceback.print_exc()
            st.stop()

        col_left_files, col_right_files = st.columns([1, 2])

        with col_left_files:
            st.subheader("Izbor Vozila i Datuma")
            # Default datum je jučerašnji
            default_file_date = (datetime.now(EU_BG) - timedelta(days=1)).date()
            selected_day = st.date_input("Datum za pretragu fajlova", value=default_file_date, key="file_date_select")
            
            search_query = st.text_input("Pretraga vozila (ime/registracija)", "", key="vehicle_search_input").lower()
            
            filtered_units = [
                u for u in units 
                if search_query in (u.get("reg", "") + u.get("name", "")).lower()
            ]
            
            if not filtered_units:
                st.warning("Nema vozila koja odgovaraju pretrazi.")
                st.stop()

            unit_options_display = []
            unit_map_for_selection = {} # Mapiranje display stringa na unit ID za lakše pronalaženje
            for i, u_item in enumerate(filtered_units):
                display_str = f"{u_item.get('reg', 'Nema Reg.')} — {u_item.get('name', 'Nema Imena')}"
                # Ako labela nije jedinstvena, dodaj ID
                if any(opt == display_str for opt in unit_options_display):
                    display_str_unique = f"{display_str} (ID: {u_item['id']})"
                    unit_options_display.append(display_str_unique)
                    unit_map_for_selection[display_str_unique] = u_item['id']
                else:
                    unit_options_display.append(display_str)
                    unit_map_for_selection[display_str] = u_item['id']
            
            if not unit_options_display:
                st.warning("Nema vozila za prikaz.")
                st.stop()
                
            selected_unit_display_str = st.radio(
                "Izaberite vozilo:", unit_options_display, index=0, key="vehicle_radio_select"
            )
            
            selected_unit_id = unit_map_for_selection.get(selected_unit_display_str)
            selected_unit = next((u for u in filtered_units if u['id'] == selected_unit_id), None)

            if not selected_unit:
                st.error("Greška pri izboru vozila. Molimo osvežite stranicu.")
                st.stop()

        with col_right_files:
            unit_display_name_files = selected_unit.get("reg") or selected_unit.get("name") or f"ID: {selected_unit['id']}"
            st.subheader(f"Fajlovi za: **{unit_display_name_files}**")
            st.markdown(f"Datum: **{selected_day.strftime('%d.%m.%Y')}**")

            try:
                listed_files = list_files(current_sid, selected_unit["id"], selected_day, wialon_api_base)
            except RuntimeError as e:
                st.error(f"Wialon API greška pri listanju fajlova: {e}")
                st.stop()
            except Exception as e:
                st.error(f"Neočekivana greška pri listanju fajlova: {e}")
                traceback.print_exc()
                st.stop()

            if not listed_files:
                st.info("Nema dostupnih fajlova za izabrano vozilo i datum.")
                st.stop()

            st.markdown("##### Izaberite fajlove za akciju:")
            
            # Inicijalizacija session state-a za checkboxove ako ne postoji
            checkbox_state_key_prefix = f"cb_sel_{selected_unit['id']}_{selected_day.isoformat()}_"
            if f"{checkbox_state_key_prefix}all" not in st.session_state:
                st.session_state[f"{checkbox_state_key_prefix}all"] = False # Za "Označi sve"
            
            # Dugmad za selekciju svih/nijednog fajla
            sel_col1, sel_col2 = st.columns(2)
            if sel_col1.button("Označi sve fajlove", key=f"sel_all_btn_{selected_unit['id']}_{selected_day}"):
                st.session_state[f"{checkbox_state_key_prefix}all"] = True
                for f_meta_item in listed_files:
                    st.session_state[f"{checkbox_state_key_prefix}{f_meta_item['n']}"] = True
                st.experimental_rerun()

            if sel_col2.button("Poništi sve oznake", key=f"desel_all_btn_{selected_unit['id']}_{selected_day}"):
                st.session_state[f"{checkbox_state_key_prefix}all"] = False
                for f_meta_item in listed_files:
                    st.session_state[f"{checkbox_state_key_prefix}{f_meta_item['n']}"] = False
                st.experimental_rerun()

            selected_file_names_for_action = []
            for f_meta_item in listed_files:
                cb_key = f"{checkbox_state_key_prefix}{f_meta_item['n']}"
                # Inicijalizuj ako nije već postavljeno (npr. ako je "Označi sve" korišćeno)
                if cb_key not in st.session_state:
                     st.session_state[cb_key] = st.session_state.get(f"{checkbox_state_key_prefix}all", False)

                file_size_kb = f_meta_item.get('s', 0) / 1024
                mod_time_ts = f_meta_item.get('mt', f_meta_item.get('ct', 0))
                mod_time_str = datetime.fromtimestamp(mod_time_ts, EU_BG).strftime('%H:%M:%S') if mod_time_ts else 'N/A'
                
                is_checked = st.checkbox(
                    f"{f_meta_item['n']} ({file_size_kb:.1f} KB, {mod_time_str})",
                    key=cb_key,
                    value=st.session_state[cb_key] # Vrednost iz session state
                )
                if is_checked:
                    selected_file_names_for_action.append(f_meta_item['n'])
                # Ažuriranje session state-a direktno iz checkboxa (Streamlit to radi automatski)
                # st.session_state[cb_key] = is_checked # Nije neophodno, Streamlit ažurira sam

            st.markdown("---")
            if not selected_file_names_for_action:
                st.info("Niste izabrali nijedan fajl.")
                st.stop()

            st.markdown(f"**Izabrano fajlova: {len(selected_file_names_for_action)}**")
            
            action_col_dl, action_col_email = st.columns(2)
            
            # Sanitizacija imena za fajlove
            safe_unit_name = "".join(c for c in unit_display_name_files if c.isalnum() or c in (' ', '_', '-')).strip().replace(' ', '_')

            with action_col_dl:
                if len(selected_file_names_for_action) == 1:
                    single_fname_dl = selected_file_names_for_action[0]
                    if st.button(f"📥 Preuzmi: {single_fname_dl}", key=f"dl_single_{single_fname_dl}"):
                        with st.spinner(f"Preuzimanje {single_fname_dl}..."):
                            file_bytes_dl = get_file(current_sid, selected_unit["id"], single_fname_dl, wialon_api_base)
                        if file_bytes_dl:
                            # Nudimo download direktno
                            st.download_button(
                                label=f"Preuzmi {single_fname_dl} sada",
                                data=file_bytes_dl,
                                file_name=single_fname_dl, # Originalno ime fajla
                                mime="application/octet-stream",
                                key=f"actual_dl_btn_s_{single_fname_dl}"
                            )
                        else:
                            st.error(f"Neuspešno preuzimanje {single_fname_dl}.")
                else: # Više fajlova -> ZIP
                    zip_dl_filename = f"{safe_unit_name}_{selected_day.strftime('%Y%m%d')}.zip"
                    if st.button(f"📥 Preuzmi ZIP ({len(selected_file_names_for_action)} fajla)", key="dl_zip_btn"):
                        zip_buffer_dl = BytesIO()
                        with st.spinner(f"Kreiranje ZIP arhive..."):
                            with zipfile.ZipFile(zip_buffer_dl, "w", zipfile.ZIP_DEFLATED) as zf_dl:
                                for fname_to_zip_dl in selected_file_names_for_action:
                                    file_bytes_zf_dl = get_file(current_sid, selected_unit["id"], fname_to_zip_dl, wialon_api_base)
                                    if file_bytes_zf_dl:
                                        zf_dl.writestr(fname_to_zip_dl, file_bytes_zf_dl)
                                    else:
                                        st.warning(f"Fajl {fname_to_zip_dl} nije dodat u ZIP (greška pri preuzimanju).")
                        zip_buffer_dl.seek(0)
                        st.download_button(
                            label=f"Preuzmi {zip_dl_filename} sada",
                            data=zip_buffer_dl,
                            file_name=zip_dl_filename,
                            mime="application/zip",
                            key="actual_dl_btn_z"
                        )
            
            with action_col_email:
                if st.button(f"📧 Pošalji E-mail ({len(selected_file_names_for_action)} fajla)", key="email_files_btn"):
                    if not all(s_settings.get(k) for k in ["server", "port", "username", "password", "recipients"]):
                        st.error("SMTP podešavanja nisu kompletna. Proverite Admin panel.")
                    else:
                        email_att_bytes = None
                        email_att_filename = ""
                        email_subject = f"DDD fajlovi: {unit_display_name_files} ({selected_day.strftime('%d.%m.%Y')})"
                        email_body = f"U prilogu su DDD fajlovi za vozilo {unit_display_name_files} za datum {selected_day.strftime('%d.%m.%Y')}.\n\nFajlovi:\n" + "\n".join(selected_file_names_for_action)

                        with st.spinner("Priprema fajlova za e-mail..."):
                            if len(selected_file_names_for_action) == 1:
                                single_fname_email = selected_file_names_for_action[0]
                                email_att_bytes = get_file(current_sid, selected_unit["id"], single_fname_email, wialon_api_base)
                                email_att_filename = single_fname_email
                            else: # ZIP za više fajlova
                                zip_buffer_email = BytesIO()
                                with zipfile.ZipFile(zip_buffer_email, "w", zipfile.ZIP_DEFLATED) as zf_email:
                                    for fname_to_email_zip in selected_file_names_for_action:
                                        file_bytes_email_zf = get_file(current_sid, selected_unit["id"], fname_to_email_zip, wialon_api_base)
                                        if file_bytes_email_zf:
                                            zf_email.writestr(fname_to_email_zip, file_bytes_email_zf)
                                zip_buffer_email.seek(0)
                                email_att_bytes = zip_buffer_email.read()
                                email_att_filename = f"{safe_unit_name}_{selected_day.strftime('%Y%m%d')}_email.zip"
                        
                        if email_att_bytes:
                            if send_mail(email_subject, email_body, email_att_bytes, email_att_filename, s_settings):
                                st.success("E-mail uspešno poslat!")
                            # Greška se prikazuje unutar send_mail
                        else:
                            st.error("Nije bilo moguće pripremiti fajlove za slanje e-mailom (greška pri preuzimanju ili nema fajlova).")
        
        st.markdown("---")
        st.caption("""
        Napomene: 
        - Za analizu sadržaja DDD fajlova (aktivnosti vozača, prekršaji itd.) koristite specijalizovani softver.
        - Ako automatsko slanje ne radi, proverite validnost SID-a (prijavite se ponovo) i SMTP podešavanja.
        - Za održavanje aktivnosti aplikacije na Streamlit Community Cloud-u, koristite eksterni "ping" servis (npr. UptimeRobot).
        """)

# ─────────── entrypoint ───────────
if __name__ == "__main__":
    main()
